'use strict';

(function () {
  var FaIcon, React, cx, pt;

  React = require('react');

  pt = require('prop-types');

  cx = require('classnames');

  FaIcon = function FaIcon(props) {
    return React.createElement('i', { className: cx('fa', 'fa-' + props.icon, props.className) });
  };

  FaIcon.propTypes = {
    icon: pt.string,
    className: pt.oneOfType([pt.string, pt.object])
  };

  module.exports = FaIcon;
}).call(undefined);

